<template>
    <div>
      <h1>Products</h1>
      <div class="products">
        <div v-for="product in products" :key="product.name">
          <div>{{ product.name }}</div>
          <div>{{ product.cost }}</div>
          <img :src="product.image" alt="product image"/>
          <button @click="addToCart(product)">Add To Cart</button>
        </div>
      </div>
  
      <h2>Cart</h2>
      <div v-if="cart.length > 0">
        <div v-for="(item, index) in cart" :key="index">
          <div>{{ item.name }} - {{ item.cost }}</div>
        </div>
        <div>Total: {{ totalCost }}</div>
      </div>
      <div v-else>
        <p>Your cart is empty.</p>
      </div>
    </div>
  </template>
  
  <script>
  export default {
    name: "App",
    data() {
      return {
        cart: [],
        products: [
          {
            name: "Surging Spark Pack",
            cost: "10$",
            image: "data:image/jpeg;base64," // replace with actual image URL
          },
          {
            name: "Stellar Crown",
            cost: "15$",
            image: "data:image/jpeg;base64," // replace with actual image URL
          }
        ]
      };
    },
    methods: {
      addToCart(product) {
        this.cart.push(product);
      }
    },
    computed: {
      totalCost() {
        return this.cart.reduce((total, product) => {
          const cost = parseFloat(product.cost.replace('$', ''));
          return total + cost;
        }, 0).toFixed(2) + '$';
      }
    }
  };
  </script>
  
  <style scoped>
  .products {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 10px;
  }
  
  button {
    padding: 10px;
    margin-top: 10px;
    background-color: #4CAF50;
    color: white;
    border: none;
    cursor: pointer;
  }
  
  button:hover {
    background-color: #45a049;
  }
  </style>
  